                {
                    $rand_video = $videos[array_rand($videos)]; //随机获取数组元素
                    $abs_dir = $dir . "/" . $client_date . "/" . $rand_video;
                    $oss_url = $oss_base_url_a . $abs_dir . $oss_base_url_b;
                    echo $oss_url;
                } else {
                    $abs_dir = $dir . "/" . $client_date . "/" . $videos[0];
                    $oss_url = $oss_base_url_a . $abs_dir . $oss_base_url_b;
                    echo $oss_url;
                }
            } catch (\Throwable $th) { //如果出现任何错误
                echo "error-03";
            }
        } else {
            echo "error-02";
        }
    } else {
        echo "error-01";
    }
} else {
    echo "error-00";
}
